drawPlot = () => {
  const { data } = this.props;

  // Transform data for Plotly
  const transformedData = [];
  const countries = Object.keys(data);

  countries.forEach(country => {
    const importData = data[country].import;
    const exportData = data[country].export;

    const importTrace = {
      x: Object.keys(importData),
      y: Object.values(importData),
      type: 'scatter',
      mode: 'lines+markers',
      name: `${country} - Import`
    };

    const exportTrace = {
      x: Object.keys(exportData),
      y: Object.values(exportData),
      type: 'scatter',
      mode: 'lines+markers',
      name: `${country} - Export`
    };

    transformedData.push(importTrace, exportTrace);
  });

  const layout = {
    title: 'Import/Export Data',
    xaxis: {
      title: 'Year'
    },
    yaxis: {
      title: 'Value'
    }
  };

  Plotly.newPlot('plot', transformedData, layout);
};
