import React from 'react';
import Plotly from 'plotly.js-basic-dist';
import LineChart from './LineChart';

class Plot extends React.Component {
  componentDidMount() {
    this.drawPlot();
  }

  componentDidUpdate(prevProps) {
    if (this.props.data !== prevProps.data) {
      this.drawPlot();
    }
  }
  drawPlot = () => {
    const { data, selectedCountries, selectedYears } = this.props;
    
    // Filter data based on selected countries and years
    const filteredData = {};
    selectedCountries.forEach(country => {
        filteredData[country] = {
            import: {},
            export: {}
        };
        selectedYears.forEach(year => {
            filteredData[country].import[year] = data[country]?.import?.[year] || null;
            filteredData[country].export[year] = data[country]?.export?.[year] || null;
        });
    });

    // Define bar chart data for import and export
    const importBarData = selectedCountries.map(country => ({
        x: selectedYears,
        y: Object.values(filteredData[country].import),
        type: 'bar',
        name: `${country} - Import`
    }));
  
    const exportBarData = selectedCountries.map(country => ({
        x: selectedYears,
        y: Object.values(filteredData[country].export),
        type: 'bar',
        name: `${country} - Export`
    }));

    const layout = {
        title: {
            text: 'Import/Export Data',
            font: {
                size: 24 // Increase the title font size
            },
            y: 0.87 // Increase the space between title and chart
        },
        xaxis: {
            title: 'Year'
        },
        yaxis: {
            title: 'Value'
        },
        hovermode: 'closest', // Display hover labels closest to the cursor
        legend: {
            orientation: 'h', // Horizontal legend
            yanchor: 'bottom',
            y: 1.05, // Increase the space between chart and legend
            xanchor: 'right',
            x: 1
        },
        margin: { // Increase space between countries' charts
            t: 100, // Top margin
            b: 150 // Bottom margin
        },
        width: 1200, // Increase chart width for higher resolution
        height: 800 // Increase chart height for higher resolution
    };
  
    Plotly.newPlot('plot', [...importBarData, ...exportBarData], layout);
}



render() {
  return (
    <div>
      <LineChart data={this.props.data} />
      <div id="plot"></div>
    </div>
  );
}
}

export default Plot;
