import React from 'react';
import Plotly from 'plotly.js-basic-dist';

class Plot extends React.Component {
  componentDidMount() {
    this.drawPlot();
  }

  componentDidUpdate(prevProps) {
    if (this.props.data !== prevProps.data) {
      this.drawPlot();
    }
  }

  drawPlot = () => {
    const { data } = this.props;
  
    // Define colors and line styles
    const colors = Plotly.d3.scale.category10().range();
    const lineStyles = ['solid', 'dot', 'dash', 'longdash', 'dashdot', 'longdashdot'];
  
    const transformedData = [];
    const countries = Object.keys(data);
  
    countries.forEach((country, index) => {
      const importData = data[country].import;
      const exportData = data[country].export;
  
      const importTrace = {
        x: Object.keys(importData),
        y: Object.values(importData),
        type: 'scatter',
        mode: 'lines+markers',
        name: `${country} - Import`,
        line: {
          color: colors[index % colors.length], // Use a different color for each country
          dash: lineStyles[index % lineStyles.length] // Use a different line style for each country
        }
      };
  
      const exportTrace = {
        x: Object.keys(exportData),
        y: Object.values(exportData),
        type: 'scatter',
        mode: 'lines+markers',
        name: `${country} - Export`,
        line: {
          color: colors[(index + 1) % colors.length], // Use a different color for export
          dash: lineStyles[(index + 1) % lineStyles.length] // Use a different line style for export
        }
      };
  
      transformedData.push(importTrace, exportTrace);
      transformedData.push({ // Add an empty trace to create space between charts
        x: [],
        y: [],
        mode: 'none',
        marker: { opacity: 0 },
        name: '',
        showlegend: false
      });
    });
  
    const layout = {
      title: 'Import/Export Data',
      xaxis: {
        title: 'Year'
      },
      yaxis: {
        title: 'Value'
      },
      hovermode: 'closest', // Display hover labels closest to the cursor
      legend: {
        orientation: 'h', // Horizontal legend
        yanchor: 'bottom',
        y: 1.02,
        xanchor: 'right',
        x: 1
      },
      margin: { // Increase space between charts
        t: 50, // Top margin
        b: 50 // Bottom margin
      }
    };
  
    Plotly.newPlot('plot', transformedData, layout);
  };
  
    

  render() {
    return <div id="plot"></div>;
  }
}

export default Plot;
