import React from 'react';
import Plotly from 'plotly.js-basic-dist';

class Plot extends React.Component {
  componentDidMount() {
    this.drawPlot();
  }

  componentDidUpdate(prevProps) {
    if (this.props.data !== prevProps.data) {
      this.drawPlot();
    }
  }
  drawPlot = () => {
    const { data, selectedCountries, selectedYears } = this.props;
  
    // Define a custom color scale for vibrant colors
    const colors = [
      '#FF0000', // Pure red
      '#FFFF00', // Pure yellow
      '#00FF00', // Pure green
      '#0000FF', // Pure blue
      '#FF00FF', // Pure magenta
      '#FF6347', // Tomato
      '#00FFFF', // Aqua
      '#FFA500', // Orange
      '#800080', // Purple
      '#40E0D0'  // Turquoise
    ];
  
    // Define line styles
    const lineStyles = [
      'solid', // Solid line
      'dashed', // Dashed line
      'dotted', // Dotted line
      'dashdot', // Alternating dashes and dots
      'longdashdotdot', // Alternating long dashes and double dots
      'shortdash' // Short dashed line
    ];
  
    // Define line chart data for all countries' import and export
    const lineChartData = Object.keys(data).map((country, index) => ({
      x: selectedYears,
      y: Object.values(data[country].import),
      type: 'scatter',
      mode: 'lines+markers',
      name: `${country} - Import`,
      line: {
        color: colors[index % colors.length], // Use a different color for each country
        dash: lineStyles[index % lineStyles.length] // Use a different line style for each country
      }
    }), Object.keys(data).map((country, index) => ({
      x: selectedYears,
      y: Object.values(data[country].export),
      type: 'scatter',
      mode: 'lines+markers',
      name: `${country} - Export`,
      line: {
        color: colors[index % colors.length], // Use a different color for each country
        dash: lineStyles[index % lineStyles.length] // Use a different line style for each country
      }
    })));
  
    // Filter data based on selected countries and years
    const filteredData = {};
    selectedCountries.forEach(country => {
      filteredData[country] = {
        import: {},
        export: {}
      };
      selectedYears.forEach(year => {
        filteredData[country].import[year] = data[country]?.import?.[year] || null;
        filteredData[country].export[year] = data[country]?.export?.[year] || null;
      });
    });
  
    // Define bar chart data for import and export
    const importBarData = selectedCountries.map(country => ({
      x: selectedYears,
      y: Object.values(filteredData[country].import),
      type: 'bar',
      name: `${country} - Import`
    }));
  
    const exportBarData = selectedCountries.map(country => ({
      x: selectedYears,
      y: Object.values(filteredData[country].export),
      type: 'bar',
      name: `${country} - Export`
    }));
  
    const layout = {
      title: 'Import/Export Data',
      xaxis: { title: 'Year' },
      yaxis: { title: 'Value' },
      barmode: 'group',
      width: 1200,
      height: 800
    };
  
    Plotly.newPlot('plot', [...lineChartData, ...importBarData, ...exportBarData], layout);
  };





  render() {
    return <div id="plot"></div>;
  }
}

export default Plot;
